import React, { useState } from 'react'

const SignUp = () => {
  const [adName, setAdName] = useState('')
  const [adEmail, setAdEmail] = useState('')
  const [adPassword, setAdPassword] = useState('')

  const handleNameChange = (e) => {
    setAdName(e.target.value)
  }

  const handleEmailChange = (e) => {
    setAdEmail(e.target.value)
  }

  const handlePasswordChange = (e) => {
    setAdPassword(e.target.value)
  }

  const handleSubmit = (e) => {
    fetch('http://localhost:5000/api/admin/signUp', {
      method : 'POST',
      body : JSON.stringify({
        name : adName,
        email : adEmail,
        passord : adPassword
      }),
      headers : 'content-type : application/json ; charset = UTF-8'
    })
    .then((response) => response.json())
    .catch((err) => {console.log(err.message)})
  }

  return (
    <div>
      <label>Name</label>
      <input type='text' value={adName} placeholder='Enter Name' onChange={(e) => {handleNameChange(e)}} required/>
      <label>Email</label>
      <input type='text' value={adEmail} placeholder='Enter Email' onChange={(e) => {handleEmailChange(e)}} required/>
      <label>Password</label>
      <input type='text' value={adPassword} placeholder='Enter Password' onChange={(e) => {handlePasswordChange(e)}} required/>
      <button>Submit</button>
    </div>
  )
}

export default SignUp
